import React, { useEffect, useState, useRef } from 'react';
import { Card, CardContent, Typography, Link, CircularProgress, Button } from '@mui/material';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import '../App.css';

const RedditPosts = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  const renderContent = (post, index) => { // Add index here
    const contentRef = useRef(null); // Ref for content element

    useEffect(() => {
      if (contentRef.current) {
        setPosts((prevPosts) => {
          const updatedPosts = [...prevPosts];
          updatedPosts[index].showReadMore = contentRef.current.scrollHeight > 200; // Check rendered height
          return updatedPosts;
        });
      }
    }, [post.selftext, index]); // Re-run effect when selftext changes

    if (post.selftext) {
      return (
        <div ref={contentRef}> {/* Add ref here */}
          <ReactMarkdown remarkPlugins={[remarkGfm]}>{post.selftext}</ReactMarkdown>
        </div>
      );
    }
    return <p>No content available.</p>;
  };

  const handleReadMore = (index) => {
    setPosts((prevPosts) => {
      const updatedPosts = [...prevPosts];
      updatedPosts[index].expanded = !updatedPosts[index].expanded;
      updatedPosts[index].cardHeight = updatedPosts[index].expanded ? 'auto' : '200px';
      return updatedPosts;
    });
  };

  return (
    <div className="container">
      {loading ? (
        <div className="loader-container">
          <Typography variant="h6" className="loading-text">Loading...</Typography>
          <CircularProgress />
        </div>
      ) : (
        posts.map((post, index) => (
          <div className="card-container" key={index}>
            <Card className="card" style={{ height: post.cardHeight, overflow: 'hidden' }}>
              <CardContent className="cardContent">
                <Typography variant="h6" component="h2">
                  {post.title}
                </Typography>

                <div className="selftext">
                  {renderContent(post)}
                </div>

                {post.selftext && post.selftext.length > 200 && ( // Conditionally render "Read More"
                  <Button onClick={() => handleReadMore(index)}>
                    {post.expanded ? "Read Less" : "Read More"}
                  </Button>
                )}

                <Typography variant="body2" color="textSecondary">
                  Score: {post.score}
                </Typography>

                <Link href={post.url} target="_blank" rel="noopener noreferrer" className="post-url">
                  Go to post
                </Link>
              </CardContent>
            </Card>
          </div>
        ))
      )}
    </div>
  );
};

export default RedditPosts;
