import React, { useEffect, useState } from 'react';
import { Card, CardContent, Typography, Link, CircularProgress } from '@mui/material';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import '../App.css';

const RedditPosts = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('https://www.reddit.com/r/reactjs.json')
      .then((response) => response.json())
      .then((data) => {
        const postsData = data.data.children.map((post) => ({
          title: post.data.title,
          selftext: post.data.selftext, // Use selftext for Markdown if available
          selftext_html: post.data.selftext_html || '', // Keep for fallback or if only HTML is available.
          url: post.data.url,
          score: post.data.score,
        }));
        setPosts(postsData);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
        setLoading(false);
      });
  }, []);

  const renderContent = (post) => {
    // 1. Try to render Markdown first (preferred)
    if (post.selftext) {
      return <ReactMarkdown remarkPlugins={[remarkGfm]}>{post.selftext}</ReactMarkdown>;
    }
    // 2. Handle cases where there's no content
    return <p>No content available.</p>;
  };


  return (
    <div className="container">
      {loading ? (
        <div className="loader-container">
          <Typography variant="h6" className="loading-text">Loading...</Typography>
          <CircularProgress />
        </div>
      ) : (
        posts.map((post, index) => (
          <div className="card-container" key={index}>
            <Card className="card">
              <CardContent>
                <Typography variant="h6" component="h2">
                  {post.title}
                </Typography>

                <div className="selftext">
                  {renderContent(post)} {/* Use the renderContent function */}
                </div>

                <Typography variant="body2" color="textSecondary">
                  Score: {post.score}
                </Typography>

                <Link href={post.url} target="_blank" rel="noopener noreferrer" className="post-url">
                  Go to post
                </Link>
              </CardContent>
            </Card>
          </div>
        ))
      )}
    </div>
  );
};

export default RedditPosts;
