import React, { useEffect, useState, useRef } from 'react';
import { Card, CardContent, Typography, Link, CircularProgress, Button } from '@mui/material';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import '../App.css';

const RedditPosts = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('https://www.reddit.com/r/reactjs.json')
      .then((response) => response.json())
      .then((data) => {
        const postsData = data.data.children.map((post) => ({
          title: post.data.title,
          selftext: post.data.selftext,
          url: post.data.url,
          score: post.data.score,
          expanded: false,
          cardHeight: '200px',
          showReadMore: false, // Initialize showReadMore
        }));
        setPosts(postsData);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
        setLoading(false);
      });
  }, []);

  const renderContent = (post, index) => {
    const contentRef = useRef(null);

    useEffect(() => {
      if (contentRef.current) {
        setPosts((prevPosts) => {
          const updatedPosts = [...prevPosts];
          updatedPosts[index].showReadMore = contentRef.current.scrollHeight > 200;
          return updatedPosts;
        });
      }
    }, [post.selftext, index]);

    if (post.selftext) {
      return (
        <div ref={contentRef}>
          <ReactMarkdown remarkPlugins={[remarkGfm]}>{post.selftext}</ReactMarkdown>
        </div>
      );
    }
    return <p>No content available.</p>;
  };

  const handleReadMore = (index) => {
    setPosts((prevPosts) => {
      const updatedPosts = [...prevPosts];
      updatedPosts[index].expanded = !updatedPosts[index].expanded;
      updatedPosts[index].cardHeight = updatedPosts[index].expanded ? 'auto' : '200px';
      return updatedPosts;
    });
  };

  return (
    <div className="container">
      {loading ? (
        <div className="loader-container">
          <Typography variant="h6" className="loading-text">Loading...</Typography>
          <CircularProgress />
        </div>
      ) : (
        posts.map((post, index) => (
          <div className="card-container" key={index}>
            <Card className="card" style={{ height: post.cardHeight, overflow: 'hidden' }}>
              <CardContent className="cardContent">
                <Typography variant="h6" component="h2">
                  {post.title}
                </Typography>

                <div className="selftext">
                  {renderContent(post, index)}
                </div>

                {post.showReadMore && (
                  <Button onClick={() => handleReadMore(index)}>
                    {post.expanded ? "Read Less" : "Read More"}
                  </Button>
                )}

                <Typography variant="body2" color="textSecondary">
                  Score: {post.score}
                </Typography>

                <Link href={post.url} target="_blank" rel="noopener noreferrer" className="post-url">
                  Go to post
                </Link>
              </CardContent>
            </Card>
          </div>
        ))
      )}
    </div>
  );
};

export default RedditPosts;
