import React, { useEffect, useState } from 'react';
import { Card, CardContent, Typography, Link, CircularProgress } from '@mui/material';
import '../App.css'; // Import the CSS file for styling

const RedditPosts = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch Reddit data
    fetch('https://www.reddit.com/r/reactjs.json')
      .then((response) => response.json())
      .then((data) => {
        const postsData = data.data.children.map((post) => ({
          title: post.data.title,
          selftext_html: post.data.selftext_html,
          url: post.data.url,
          score: post.data.score,
        }));
        setPosts(postsData);
        setLoading(false); // Data fetched, stop loading
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
        setLoading(false); // Stop loading in case of error
      });
  }, []);

  // Function to toggle card expansion
  const toggleExpand = (index) => {
    setPosts((prevPosts) =>
      prevPosts.map((post, i) =>
        i === index ? { ...post, expanded: !post.expanded } : post
      )
    );
  };

  return (
    <div className="container">
      {loading ? (
        <div className="loader-container">
          <Typography variant="h6" className="loading-text">Loading...</Typography>
          <CircularProgress />
        </div>
      ) : (
        posts.map((post, index) => (
          <div className="card-container" key={index}>
            <Card className="card">
              <CardContent>
                <Typography variant="h6" component="h2">
                  {post.title}
                </Typography>

                <div
                  className="selftext"
                  dangerouslySetInnerHTML={{
                    __html: post.expanded
                      ? post.selftext_html
                      : `${post.selftext_html.substring(0, 200)}...`,
                  }}
                />
                
                {!post.expanded && (
                  <Link
                    href="#"
                    onClick={(e) => {
                      e.preventDefault();
                      toggleExpand(index);
                    }}
                  >
                    Read more
                  </Link>
                )}

                <Typography variant="body2" color="textSecondary">
                  Score: {post.score}
                </Typography>
              </CardContent>
            </Card>
          </div>
        ))
      )}
    </div>
  );
};

export default RedditPosts;
